import { ArrowBack, Delete } from '@mui/icons-material';
import { Box, Typography, styled } from '@mui/material';
import React from 'react';
import { useOutletContext, useLocation } from 'react-router-dom';
import { emptyProfilePic } from '../constants/constants';

const Container = styled(Box)({
  marginLeft: '15px',
  width: '100%',
  display: 'flex',
});

const Wrapper = styled(Box)({
  display: 'flex',
  '&>p>span': {
    fontSize: '12px',
    color: '#5e5e5e',
  },
});
const IconWrapper = styled(Box)({
  padding: '15px',
});

const Subject = styled(Typography)({
  fontSize: '22px',
  margin: '10px 0 20px 75px',
  display: 'flex',
});

const Indicator = styled(Box)({
  fontSize: '12px',
  background: '#dddddd',
  color: '#222222',
  padding: '2px 4px',
  marginLeft: '6px',
  borderRadius: '4px',
  alignSelf: 'center',
});

const Date = styled(Box)({
  margin: '0px 50px 0 auto',
});
const ViewEmail = () => {
  const { openDrawer } = useOutletContext();
  const { state } = useLocation();
  const { email } = state;
  return (
    <Box
      style={
        openDrawer ? { marginLeft: 250, width: '100%' } : { width: '100%' }
      }
    >
      <IconWrapper>
        <ArrowBack
          onClick={() => window.history.back()}
          color="action"
          fontSize="small"
        />
        <Delete
          fontSize="small"
          color="action"
          style={{ marginLeft: '40px' }}
        />
      </IconWrapper>
      <Subject>
        {email.subject}
        <Indicator component="span">Inbox</Indicator>
      </Subject>
      <Container>
        <img src={emptyProfilePic} alt="dp" />
        <Box>
          <Wrapper>
            <Typography>
              {email.name}
              <Box component="span">&nbsp;&#60;{email.to}&#62;</Box>
            </Typography>
            <Date>
              {new window.Date(email.date).getDate()}&nbsp;
              {new window.Date(email.date).toLocaleString('default', {
                month: 'long',
              })}
              &nbsp;
              {new window.Date(email.date).getFullYear()}
            </Date>
          </Wrapper>
          <Typography>{email.body}</Typography>
        </Box>
      </Container>
    </Box>
  );
};

export default ViewEmail;
